angular.module('champions.templates', []).run(['$templateCache', function($templateCache) {
    $templateCache.put('champions/components/app/template.html', '<div class="su-champions-app su-pane"><div class="su-pane-header"><span class="su-pane-title">Champions</span><ul class="su-actions"><li ng-click="ctrl.reset()" class="su-action danger"><i class="reset">Reset</i></li></ul></div><div class="su-pane-body empty"><div class="vertical-scroll-container"><su-champions-grid champions="champions"></su-champions-grid></div></div></div>');

    $templateCache.put('champions/components/card/template.html', '<div ng-click="ctrl.select(champion)" class="su-champions-card"><div class="su-champions-card-header"><div ng-style="champion|championIcon" class="icon"></div><span class="name">{{ champion.name }}</span></div><div class="su-champions-card-body"><div class="stats"><div ng-repeat="(key, stat) in champion|stats" ng-class="{ missing: !stat }" class="stat"><div ng-style="stat|statIcon" class="icon"></div><div class="value">{{ stat|statRange }}</div></div></div></div></div>');

    $templateCache.put('champions/components/grid/template.html', '<div class="su-champions-grid"><div ng-click="ctrl.select(null)" ng-class="{ \'active\': currentChampion == null }" class="su-champions-card"><div class="su-champions-card-header"><div class="img"></div><span class="name">No Champion</span></div><div class="su-champions-card-body"><div class="empty-message"><p>Select this to use no champion data for your build.</p></div></div></div><su-champions-card ng-repeat="(id, champion) in champions" champion="champion" ng-class="{ \'active\': currentChampion == champion }"></su-champions-card></div>');
}])