angular.module('runes.templates', []).run(['$templateCache', function($templateCache) {
    $templateCache.put('runes/components/app/template.html', '<div class="su-runes-app"><su-runes-list></su-runes-list><su-runes-page></su-runes-page></div>');

    $templateCache.put('runes/components/list/template.html', '<div class="su-runes-list su-pane"><div class="su-pane-header"><span class="su-pane-title">Runes</span></div><div class="su-pane-body empty"><div class="vertical-scroll-container"><div ng-repeat="group in ctrl.groups" class="rune-group {{group.expanded ? \'expanded\' : \'collapsed\'}}"><div ng-click="ctrl.toggle($event, group)" class="rune-group-header"><span class="rune-group-name {{group.name | lowercase}}">{{ group.name }}</span></div><ul class="rune-group-runes"><su-runes-list-rune ng-repeat="rune in ctrl.runes[group.name]" rune="rune"></su-runes-list-rune></ul></div></div></div></div>');

    $templateCache.put('runes/components/list-rune/template.html', '<li ng-click="ctrl.select($event, rune)" ng-mouseenter="ctrl.showTooltip($event, rune)" ng-mouseleave="ctrl.hideTooltip($event, rune)" class="list-rune rune {{rune.type | lowercase}}"><span class="icon"></span><span class="name">{{ rune | brief }}</span><span class="cost">{{ rune.cost }} IP</span><span class="quantity">x{{ rune.quantity }}</span><su-runes-tooltip rune="rune"></su-runes-tooltip></li>');

    $templateCache.put('runes/components/page/template.html', '<div class="su-runes-page su-pane"><div class="su-pane-header"><span class="su-pane-title">Runepage</span><ul class="su-actions"><li ng-click="ctrl.reset()" class="su-action danger"><i class="reset">Reset</i></li></ul></div><div class="su-pane-body empty"><div class="runepage"><div ng-repeat="(type, runes) in ctrl.runes" class="runes"><div ng-repeat="rune in runes track by $index" ng-if="!!rune" ng-style="rune|runestyle:$index" ng-click="ctrl.refund($event, rune, $index)" class="rune {{ rune.type|lowercase }}"><su-runes-tooltip rune="rune"></su-runes-tooltip></div></div></div></div></div>');

    $templateCache.put('runes/components/tooltip/template.html', '<div ng-style="style" ng-class="{ active: active }" class="rune-tooltip {{ rune.type | lowercase }}"><div class="tier">Tier {{ rune.tier }}</div><div class="rank">{{ rune.rank }}</div><div class="name">{{ rune.name }}</div><div class="description">{{ rune.description }}</div><div class="cost">{{ rune.cost }}</div></div>');
}])